import React, { useState, useEffect } from 'react';
import { Link, Redirect } from 'react-router-dom';

function Update(props) {
    const [Id, setId] = useState(null);
    const [Name, setName] = useState(null);
    const [Likes, setLikes] = useState(null);
    const [Dislikes, setDislikes] = useState(null);
    const [Grade, setGrade] = useState(null);
    const { data } = props.location;

    useEffect(() => {
        setId(data.Id)
        setName(data.Name)
        setLikes(data.Likes)
        setDislikes(data.Dislikes)
        setGrade(data.Grade)
    }, []);

    const putPicture = () => {
        const requestOptions = {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ "Id": Id, "Name": Name, "Likes": parseInt(Likes)+1, "Dislikes": parseInt(Dislikes), "Grade": parseInt( Grade )})
        };
        fetch("http://localhost:17499/Picture", requestOptions)
            .then(response => response.json());
    }
    return (
       
            <div className="form-group">
                <div className="col-md-offset-2 col-md-10">
                    <Link type="submit" onClick={putPicture } className="btn btn-default" to={{ pathname: '/' }}>Изменить</Link>
                </div>
            </div>
      
   );
}

export { Update };
