import React, { useState, useEffect } from 'react';
import { Link, Redirect } from 'react-router-dom';

function Home() {
    const [pictures, setPicture] = useState([]);

    useEffect(() => {
        fetch(`http://localhost/Mysite`)
            .then((response) => response.json())
            .then((result) => setPicture(result))
    }, []);


    const DeletePicture = (e) => {
        const id = e.target.elements.id.value;
        const requestOptions = {
            method: 'DELETE',
            headers: { 'Content-Type': 'application/json' },
        };
        fetch(`http://localhost/MySite/pictures/${id}`, requestOptions);
    }

    return (
        <div>
            <h3>������� ��������</h3>
            <table className="table table-bordered">
                <thead>
                    <tr className="table-dark">
                        <td>��� �����</td>
                        <td>���������� ������</td>
                        <td>���������� ���������</td>
                        <td>������ ��������</td>
                        <td></td>
                        <td></td>
                    </tr>
                </thead>
                {pictures.map(picture => (
                    <tbody>
                        <tr>
                            <td>{picture.Name}</td>
                            <td>{picture.Likes}</td>
                            <td>{picture.Dislikes}</td>
                            <td>{picture.Grade}</td>
                            <tr><Link className="btn btn-default" to={{ pathname: '/Update', data: picture }}>�������</Link></tr>
                            <td><form onSubmit={DeletePicture}>
                                <input type="hidden" value={picture.id} name="id" />
                                <button className="btn btn-default">�������</button>
                            </form>
                            </td>
                        </tr>
                    </tbody>
                ))}
            </table>
        </div>   
   );

}
export { Home };
